{"title":"APM service map by vega","type":"vega","aggs":[],"params":{"spec":"{
  $schema: https://vega.github.io/schema/vega/v5.json
  title: Service map
  autosize: {
    "type": "fit",
    "contains": "padding"
  }
  padding: 20
  "data": [
    {
      "name": "service-status",
      url: {
        index: service-status
        body: {
          size: 100
          query: {
            "match_all": {}          
          }
        }
      }
      "format": {"property": "hits.hits"},
      "transform": [
        {
          "type": "formula",
          "as": "name",
          "expr": "datum._source.service.name"
        },
        {
          "type": "formula",
          "as": "total_errors",
          "expr": "datum._source.total_errors"
        },
        {
          "type": "formula",
          "as": "total_tx",
          "expr": "datum._source.total_tx"
        },
        {
          "type": "project",
          "fields": [
            "name",
            "total_errors",
            "total_tx"
          ],
          "as": ["name", "total_errors", "total_tx"]
        }
      ]
      },
    {
      "name": "service_1",
      url: {
        %context%: true
        %timefield%: @timestamp
        index: traces-apm*,apm-*
        body: {
          size: 0,
          "aggs": {
    "service": {
      "terms": {
        "field": "service.name",
        "order": {
          "_count": "desc"
        },
        "size": 20
      }
    }
  }
        }
      }
      "format": {"property": "aggregations.service.buckets"},
      "transform": [
        {
          "type": "formula",
          "as": "name",
          "expr": "datum.key"
        }
      ]
    },
    {
      "name": "service_2",
      url: {
        %context%: true
        %timefield%: @timestamp
        index: traces-apm*,apm-*
        body: {
          size: 0,
          "aggs": {
    "service": {
      "terms": {
        "field": "span.destination.service.name",
        "order": {
          "_count": "desc"
        },
        "size": 20
      }
    }
  }
        }
      }
      "format": {"property": "aggregations.service.buckets"},
      "transform": [
        {
          "type": "formula",
          "as": "name",
          "expr": "datum.key"
        }
      ]
    },
    {
      "name": "service_3",
      url: {
        %context%: true
        %timefield%: @timestamp
        index: traces-apm*,apm-*
        body: {
          size: 0,
          "aggs": {
    "service": {
      "terms": {
        "field": "cloud.origin.service.name",
        "order": {
          "_count": "desc"
        },
        "size": 20
      }
    }
  }
        }
      }
      "format": {"property": "aggregations.service.buckets"},
      "transform": [
        {
          "type": "formula",
          "as": "name",
          "expr": "datum.key"
        }
      ]
    },
    {
      "name": "status",
      "source": ["service_1", "service_2", "service_3"]
    },
    {
      "name": "resources",
      url: {
        index: services
        body: {
          size: 100
          query: {
            "bool": {
      "must": [],
      "filter": [
        {
          "bool": {
            "should": [
              {
                "match_phrase": {
                  "process": "apm services"
                }
              }
            ],
            "minimum_should_match": 1
          }
        }
      ],
      "should": [],
      "must_not": []
    }
          }
        }
      }
      "format": {"property": "hits.hits"},
      "transform": [
        {
          "type": "formula",
          "as": "name",
          "expr": "datum._source.service.name"
        },
        {
          "type": "formula",
          "as": "type",
          "expr": "datum._source.type"
        },
        {
          "type": "formula",
          "as": "connected_to",
          "expr": "datum._source.connected_to"
        },
        {
          "type": "formula",
          "as": "connected_via",
          "expr": "datum._source.connected_via"
        },
        {
          "type": "formula",
          "as": "size",
          "expr": "1"
        },
        {
          "type": "formula",
          "as": "level",
          "expr": "datum._source.level"
        },
        {
          "type": "project",
          "fields": [
            "name",
            "type",
            "connected_to",
            "connected_via",
            "size",
            "level"
          ],
          "as": ["name", "type", "connected_to", "connected_via","size","level"]
        }
      ]
      },
      {
        name: nodes
        source: resources
        transform: [
        {
            "type": "aggregate",
            "groupby": ["type", "name"],
            "fields": ["size"],
            "as": ["size"]
          },
          {type: "formula", expr: "1", as: "size"},
          {
            type: stack
            groupby: ["type"]
            sort: {field: "name", order: "descending"}
            field: size
            offset: center
          },
          {type: "formula", expr: "(datum.y0)+0.5", as: "y0"},
          {type: "formula", expr: "(datum.y0+datum.y1)/2", as: "yc"},
           {
            type: lookup
            from: status
            key: name
            fields: ["name"]
            as: ["status"]
          },
          {
            type: lookup
            from: service-status
            key: name
            fields: ["name"]
            as: ["metrics"]
          },
          {type: "formula", expr: "(datum.status == null ? 0 : datum.status.doc_count)", as: "status"}
          {type: "formula", expr: "(datum.metrics == null ? 0 : datum.metrics.total_errors)", as: "errors"}
          {type: "formula", expr: "(datum.metrics == null ? 0 : datum.metrics.total_tx)", as: "tx"}
        ]
      },
      {
        name: links
        source: resources
        transform: [
          // find corresponding node from the right stack, keep it as "target"
          {
            type: lookup
            from: nodes
            key: name
            fields: ["connected_to"]
            as: ["target"]
          },
          {
            type: lookup
            from: nodes
            key: name
            fields: ["name"]
            as: ["source"]
          },

          {"type": "filter", "expr": "datum.target != null"}
          {
          type: formula
          expr: datum.target == null ? 0 : datum.target.yc
          as: targetYC
          },
          {
          type: formula
          expr: datum.target == null ? "" : datum.target.type
          as: targetType
          },
          {
          type: formula
          expr: "'Connection between ' + datum.target.name + ' and ' + datum.name + ' via ' + datum.connected_via"
          as: tooltip
          }
          // calculate SVG link path between stk1 and stk2 stacks for the node pair
          {
            type: linkpath
            orient: horizontal
            shape: diagonal
            sourceY: {expr: "scale('y', datum.source.yc)"}
            sourceX: {expr: "scale('x', datum.type) + bandwidth('x')"}
            targetY: {expr: "scale('y', datum.targetYC)"}
            targetX: {expr: "scale('x', datum.targetType)"}
         }
        ]
      }
    ]

  scales: [
    {
      // calculates horizontal stack positioning
      name: x
      type: band
      range: width
      domain: {data: "nodes", field: "type"}
      paddingOuter: 0.3
      paddingInner: 0.90
    },
    {
      // this scale goes up as high as the highest y1 value of all nodes
      name: y
      type: linear
      range: height
      domain: {data: "nodes", field: "y1"}
    },
    {
      name: lineColor
      type: ordinal
      range: category
      domain: {data: "resources", field: "connected_via"}
    }
  ]

  "legends": [
    {
      "title": "Connection types"
      "fill": "lineColor",
      "orient": "top-left",
      "encode": {
        "title": {"update": {"fontSize": {"value": 14}}},
        "labels": {
          "interactive": true,
          "update": {"fontSize": {"value": 12}, "fill": {"value": "black"}},
          "hover": {"fill": {"value": "firebrick"}}
        },
        "symbols": {"update": {"stroke": {"value": "transparent"}}},
        "legend": {"update": {}}
      }
    }
  ]

  marks: [
    {
      // draw the connecting line between stacks
      type: path
      name: linksMark
      from: {data: "links"}

      encode: {
        enter: {
          "strokeWidth": {"value": 5}
          "tooltip": [
            {"signal": 'datum.tooltip'}          ]
        }
        update: { 
          path: {field: "path"}
          stroke: [
            {scale: "lineColor", field: "connected_via"}
          ]

        }

      }
    },
    {
      // draw stack groups
      type: rect
      name: groupMark
      from: {data: "nodes"}
      encode: {
        enter: {
          "fill": [
            {"test": "datum.status > 0", "value": "green"},
            {"value": "gray"}
          ],
          width: {scale: "x", band: 1},
          cursor: {"value": "pointer"},
          "tooltip": [
            {"signal": "{'Name': datum.name, 'Unique Transactions': datum.tx,'Errors': datum.errors, 'Error rate': round(datum.errors/datum.tx*100) + '%'}"
            }
          ]
        }
        update: {
          x: {scale: "x", field: "type"}
          y: {scale: "y", field: "y0"}
          y2: {scale: "y", field: "y1"}
        }
      }
    },
    {

      type: text
      from: {data: "nodes"}
      // don't process events for the labels - otherwise line mouseover is unclean
      interactive: false
      encode: {
        update: {
          // depending on which stack it is, position x with some padding
          x: {
            signal: scale('x', datum.type) + (bandwidth('x'))
          }
          // middle of the group
          yc: {signal: "scale('y', datum.yc)-5"}
          fontWeight: {value: "bold"}
          // only show text label if the group's height is large enough
          text: {signal: "datum.name"}
        }
      }
    }
  ]
}
"}}

